import { useEffect, useState } from 'react';
import './TopBar.scss';
import {
  FaFacebookSquare,
  FaTwitterSquare,
  FaPinterestSquare,
  FaInstagramSquare,
  FaSearch,
} from 'react-icons/fa';

const TopBar = () => {
  const iconStyle = {
    fontSize: '22px',
    marginRight: '15px',
    color: '#181818',
    cursor: 'pointer',
  };

  const [topScrolled, setTopScrolled] = useState(false);

  const topToggler = () => {
    const scrolled = document.documentElement.scrollTop;
    if (scrolled > 20) {
      setTopScrolled(true);
    } else {
      setTopScrolled(false);
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', topToggler);
  });

  return (
    <div className={topToggler ? 'top-bar scroller' : 'topbar'}>
      <div className="top-bar__left">
        <FaFacebookSquare style={iconStyle} />
        <FaTwitterSquare style={iconStyle} />
        <FaPinterestSquare style={iconStyle} />
        <FaInstagramSquare style={iconStyle} />
      </div>
      <div className="top-bar__center">
        <ul className="top-bar__nav-links">
          <li className="top-bar__link">Home</li>
          <li className="top-bar__link">About</li>
          <li className="top-bar__link">Contact</li>
          <li className="top-bar__link">Write</li>
          <li className="top-bar__link">Logout</li>
        </ul>
      </div>
      <div className="top-bar__right">
        <FaSearch style={iconStyle} />
        <img
          src="./images/profile.jpg"
          alt="profile"
          className="top-bar__profile"
        />
      </div>
    </div>
  );
};

export default TopBar;
